mapper.insert:

INSERT INTO book_request_board (  bk_category
								, brd_comment
								, uuid
								, brb_regdate
								, brb_cnt
								, brb_title
								, brb_price
								, brb_bookname )
						VALUES(   #{bk_category}
								, #{brd_comment}
								, #{uuid}
								, now()
								, #{brb_cnt}
								, #{brb_title}
								, #{brb_price}
								, #{brb_bookname} );

mapper.select:
SELECT
	 	*
FROM book_request_board WHERE brb_id = #{brb_id};


UPDATE	book_request_board set brb_cnt = brb_cnt + 1 WHERE brb_id = #{brb_id};
